package Arrays;

public class Pattern1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        int i,j,k;
        for(i=1;i<=5;i++) {
        	for(j=1;j<=5;++j) {
        		System.out.println(" ");
        for(k=i;k>=5;k++) {
        	System.out.println("*");
        }
	}
        }
	}
	
}
