package sample;

public class OperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int a=20;
      
      int b=-20;
      
      boolean c=true;
      
      boolean d=false;
      
    	  System.out.println(~a);
    	  System.out.println(~b);
    	  System.out.println(!c);
    	  System.out.println(!d);
    	  
}
}